import {CHANGE_SEARCH_FIELD,
    REQUEST_ROBOTS_PENDING,
    REQUEST_ROBOTS_SCUCCESS,
    REQUEST_ROBOTS_FAILED
} from './constants';

const initalStateSearch={
    searchfield:''
}

export const searchRobots=(state=initalStateSearch,action={})=>{
    switch(action.type){
        case CHANGE_SEARCH_FIELD:
        return Object.assign({},state,{searchfield: action.payload})
        default: 
        return state;
    }

} 

const initalStateRobots={
    isPending:false,
    robots:[],
    error:''
}


export const requestRobots=(state=initalStateRobots,action={})=>{
    switch(action.type){
        case REQUEST_ROBOTS_PENDING:
            return Object.assign({},state,{isPending:true})
        case REQUEST_ROBOTS_SCUCCESS:
            return Object.assign({},state,{robots:action.payload,isPending:false})
        case REQUEST_ROBOTS_FAILED:
            return Object.assign({},state,{error:action.payload,isPending:false})
        default:
            return state;
    }
}
